import React, { useState } from "react";
import './Banner.css';
import DropdownListStartPlace from "./DropdownListStartPlace";
import DropdownListDestination from "./DropdownListDestination";
import Calendar from "./Calendar";
import { Link } from 'react-router-dom';

const Main = (props) => {
    const [startPlace, setStartPlace] = useState("");
    const handleChange =(e) => {
        setStartPlace(e.target.value);
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        props.onSubmit(startPlace);
    }
    return (
        <section className='Main'>
            <div className="overlay-banner"></div>
            <div className="backgroundImage" style={{ backgroundImage: "url('/background.jpg')" }}></div>
            <div className="mainContent container">
                <div className="cardDiv grid">
                    <span className="textDiv">Tìm kiếm chuyến đi</span>
                </div>
                <div className="banner">
                    <form action="#">
                        <div className="form-row">
                            <DropdownListStartPlace value = {startPlace} onChange={handleChange}/>
                            <img src="/flip.png" className="flip-icon" alt="flip" />
                            <DropdownListDestination />
                            <Calendar/>
                        </div>
                    </form>
                    <Link to="/order-tickets"><button className="btn-search" onSubmit={handleSubmit}>Tìm chuyến</button></Link>
                </div>
            </div>

        </section>

    );
}

export default Main;
