import React, { useState } from "react";
import './Main.css';

const Main = () => {
    const options = ["Hà Nội", "Hồ Chí Minh", "Đà Nẵng", "Hải Phòng", "Cần Thơ", "Huế"];

    const [selectedOption, setSelectedOption] = useState("");
    const [inputValue, setInputValue] = useState("");
    const [showOptions, setShowOptions] = useState(false);

    const handleInputChange = (event) => {
        setInputValue(event.target.value);
        setShowOptions(true);
    };

    const handleOptionSelect = (option) => {
        setSelectedOption(option);
        setInputValue(option);
        setShowOptions(false);
    };

    return (
        <section className='Main'>
            <div className="overlay"></div>
            <div className="backgroundImage" style={{ backgroundImage: "url('/background.jpg')" }}></div>
            <div className="mainContent container">
                <div className="cardDiv grid">
                    <span className="textDiv">Tìm kiếm chuyến đi</span>
                </div>
                <div className="banner">
                    <form action="#">
                        <div className="form-row">
                            <div className="input-data">
                                <input
                                    type="text"
                                    value={inputValue}
                                    onChange={handleInputChange}
                                    required
                                />
                                <label htmlFor="">Nơi đến</label>
                                {showOptions && (
                                    <ul className="autocomplete-list">
                                        {options
                                            .filter(option => option.toLowerCase().includes(inputValue.toLowerCase()))
                                            .map((option, index) => (
                                                <li
                                                    key={index}
                                                    onClick={() => handleOptionSelect(option)}
                                                >
                                                    {option}
                                                </li>
                                            ))}
                                    </ul>
                                )}
                            </div>
                            <img src="/flip.png" className="flip-icon" alt="flip" />
                            <div className="input-data">
                            <input type="text" required />
                                <label htmlFor="">Nơi xuất phát</label>
                            </div>
                            <img src="/calendar.png" className="calendar-icon" alt="calendar" />
                            <div className="input-data">
                            <input type="text" required />
                                <label htmlFor="">Ngày đi</label>
                            </div>
                        </div>
                    </form>
                    <button className="btn-search">Tìm chuyến</button>
                </div>
            </div>
        </section>
    );
}

export default Main;
