import React, { useState } from "react";
import './Main.css';
import Dropdown from 'react-bootstrap/Dropdown';
import 'bootstrap/dist/css/bootstrap.min.css';


const Main = () => {
    const [isOpen, setIsOpen] = useState(false);
  const [selectedOption, setSelectedOption] = useState(null);

  const options = ['Option 1', 'Option 2', 'Option 3'];

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const handleOptionSelect = (option) => {
    setSelectedOption(option);
    setIsOpen(false);
  };


    return (
        <section className='Main'>
            <div className="overlay"></div>
            <div className="backgroundImage" style={{ backgroundImage: "url('/background.jpg')" }}></div>
            <div className="mainContent container">
                <div className="cardDiv grid">
                    <span className="textDiv">Tìm kiếm chuyến đi</span>
                </div>
                <div className="banner">
                    <form action="#">
                        <div className="form-row">
                            {/* <div className="input-data">
                            <DropDownListComponent id="ddlelement" dataSource={options} />
                                <label htmlFor="">Nơi xuất phát</label>
                            </div> */}
                            <div className="custom-dropdown-container" onClick={toggleDropdown}>
      {selectedOption || 'Select an option'}
      {isOpen && (
        <ul className="custom-dropdown-list">
          {options.map((option, index) => (
            <li
              key={index}
              className="custom-dropdown-item"
              onClick={() => handleOptionSelect(option)}
            >
              {option}
            </li>
          ))}
        </ul>
      )}
    </div>
                            <img src="/flip.png" className="flip-icon" alt="flip" />
                            <div className="input-data">
                                <input type="text" required />
                                <label htmlFor="">Nơi đến</label>
                            </div>
                            <img src="/calendar.png" className="calendar-icon" alt="calendar" />
                            <div className="input-data">
                                <input type="text" required />
                                <label htmlFor="">Ngày đi</label>
                            </div>
                        </div>
                    </form>
                    <button className="btn-search">Tìm chuyến</button>
                </div>
            </div>
        </section>

    );
}

export default Main;
