import React, { useState, useEffect, useRef } from 'react';
import './DropdownList.css';

function DropdownList() {
  const [openDropdowns, setOpenDropdowns] = useState([]);
  const [selectedOptions, setSelectedOptions] = useState({});
  const options = {
    dropdown1: ['Option 1', 'Option 2', 'Option 3'],
    dropdown2: ['Item 1', 'Item 2', 'Item 3'],
  };

  const handleOptionSelect = (dropdownId, option) => {
    setSelectedOptions((prevState) => ({
      ...prevState,
      [dropdownId]: option,
    }));
  };

  let menuRefs = {
    dropdown1: useRef(),
    dropdown2: useRef(),
  };

  useEffect(() => {
    let handler = (e, dropdownId) => {
      if (!menuRefs[dropdownId].current.contains(e.target)) {
        setOpenDropdowns((prevOpenDropdowns) =>
          prevOpenDropdowns.filter((id) => id !== dropdownId)
        );
      }
    };

    Object.keys(menuRefs).forEach((dropdownId) => {
      document.addEventListener('mousedown', (e) => handler(e, dropdownId));
    });

    return () => {
      Object.keys(menuRefs).forEach((dropdownId) => {
        document.removeEventListener('mousedown', (e) => handler(e, dropdownId));
      });
    };
  }, [menuRefs]);

  const toggleDropdown = (dropdownId) => {
    setOpenDropdowns((prevOpenDropdowns) =>
      prevOpenDropdowns.includes(dropdownId)
        ? prevOpenDropdowns.filter((id) => id !== dropdownId)
        : [...prevOpenDropdowns, dropdownId]
    );
  };

  return (
    <div className="dropdown-list-container">
      <div className="dropdown-wrapper" ref={menuRefs.dropdown1}>
        <div
          className={`dropdown-toggle ${openDropdowns.includes('dropdown1') ? 'open' : ''}`}
          onClick={() => toggleDropdown('dropdown1')}
        >
          {selectedOptions.dropdown1 || 'Select an option'}
        </div>
        {openDropdowns.includes('dropdown1') && (
          <div className="dropdown-menu">
            {options.dropdown1.map((option, index) => (
              <div
                key={index}
                className="dropdown-item"
                onClick={() => handleOptionSelect('dropdown1', option)}
              >
                {option}
              </div>
            ))}
          </div>
        )}
      </div>

      <div className="dropdown-wrapper" ref={menuRefs.dropdown2}>
        <div
          className={`dropdown-toggle ${openDropdowns.includes('dropdown2') ? 'open' : ''}`}
          onClick={() => toggleDropdown('dropdown2')}
        >
          {selectedOptions.dropdown2 || 'Select an option'}
        </div>
        {openDropdowns.includes('dropdown2') && (
          <div className="dropdown-menu">
            {options.dropdown2.map((option, index) => (
              <div
                key={index}
                className="dropdown-item"
                onClick={() => handleOptionSelect('dropdown2', option)}
              >
                {option}
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default DropdownList;
