import React, { useState } from "react";
import './Main.css';
import Dropdown from 'react-bootstrap/Dropdown';
import 'bootstrap/dist/css/bootstrap.min.css';


const Main = () => {
    const options = ["Hà Nội", "Hồ Chí Minh", "Đà Nẵng", "Hải Phòng", "Cần Thơ", "Huế"];

    const handleOriginSelect = (option) => {
        // Handle selected origin option here
        console.log("Selected origin:", option);
    };

    return (
        <section className='Main'>
            <div className="overlay"></div>
            <div className="backgroundImage" style={{ backgroundImage: "url('/background.jpg')" }}></div>
            <div className="mainContent container">
                <div className="cardDiv grid">
                    <span className="textDiv">Tìm kiếm chuyến đi</span>
                </div>
                <div className="banner">
                    <form action="#">
                        <div className="form-row">
                            {/* <div className="input-data">
                            <DropDownListComponent id="ddlelement" dataSource={options} />
                                <label htmlFor="">Nơi xuất phát</label>
                            </div> */}
                            <Dropdown.Menu show>
      <Dropdown.Header>Dropdown header</Dropdown.Header>
      <Dropdown.Item eventKey="2">Another action</Dropdown.Item>
      <Dropdown.Item eventKey="3">Something else here</Dropdown.Item>
    </Dropdown.Menu>
                            <img src="/flip.png" className="flip-icon" alt="flip" />
                            <div className="input-data">
                                <input type="text" required />
                                <label htmlFor="">Nơi đến</label>
                            </div>
                            <img src="/calendar.png" className="calendar-icon" alt="calendar" />
                            <div className="input-data">
                                <input type="text" required />
                                <label htmlFor="">Ngày đi</label>
                            </div>
                        </div>
                    </form>
                    <button className="btn-search">Tìm chuyến</button>
                </div>
            </div>
        </section>

    );
}

export default Main;
