import React, { useState, forwardRef } from 'react';
import Dropdown from 'react-bootstrap/Dropdown';
import './DropdownList.css'; // Import your custom CSS file
import handleClickOutside from './HandleClickEvent';

const DropdownList = forwardRef(({ open, setOpen }, ref) => {
  const [selectedOption, setSelectedOption] = useState(null);

  const options = ['Option 1', 'Option 2', 'Option 3'];

  const handleOptionSelect = (option) => {
    setSelectedOption(option);
  };

  return (
    <div className="custom-dropdown-container" ref={ref}>
      
      <Dropdown>
        <Dropdown.Toggle className="custom-dropdown-toggle" id="dropdown-basic">
          {selectedOption || 'Select an option'}
        </Dropdown.Toggle>
        
        <Dropdown.Menu className="custom-dropdown-menu">
          {options.map((option, index) => (
            <Dropdown.Item
              key={index}
              className="custom-dropdown-item"
              onClick={() => {
                handleOptionSelect(option)
                setOpen(false);
              }}
            >
            
              {option}
            </Dropdown.Item>
            
          ))}
        </Dropdown.Menu>
        
      </Dropdown>
      
    </div>
  );
});


export default handleClickOutside(DropdownList);
