import React, {useState, useEffect, useRef} from 'react';
import Dropdown from 'react-bootstrap/Dropdown';
import './DropdownList.css';

function DropdownList() {
  const [open, setOpen] = useState(false);
  const [selectedOption, setSelectedOption] = useState(null);

  const options = ['Option 1', 'Option 2', 'Option 3'];

  const handleOptionSelect = (option) => {
    setSelectedOption(option);
  };

  let menuRef = useRef();

  useEffect(() => {
    let handler = (e)=>{
      if(!menuRef.current.contains(e.target)){
        setOpen(false);
        console.log(menuRef.current);
      }      
    };

    document.addEventListener("mousedown", handler);
    

    return() =>{
      document.removeEventListener("mousedown", handler);
    }

  });


  return (
    <div className="custom-dropdown-container" ref={menuRef} >
      <Dropdown >
        <Dropdown.Toggle className={`custom-dropdown-toggle ${open? 'active' : 'inactive'}`} id="dropdown-basic" >
          {selectedOption || 'Select an option'}
        </Dropdown.Toggle>

        <Dropdown.Menu className="custom-dropdown-menu">
          {options.map((option, index) => (
            <Dropdown.Item
              key={index}
              className="custom-dropdown-item"
              onClick={() => {
                handleOptionSelect(option); // Close the dropdown when an option is clicked
              }}
            >
              {option}
            </Dropdown.Item>
          ))}
        </Dropdown.Menu>
      </Dropdown>
    </div>
  );
}

export default DropdownList; // Wrap with handleClickOutside HOC
