import React, { useState, forwardRef, useEffect } from 'react';
import Dropdown from 'react-bootstrap/Dropdown';
import './DropdownList.css';
import handleClickOutside from './HandleClickEvent'; // Import the handleClickOutside HOC

const DropdownList = forwardRef(({ open, setOpen }, ref) => {
  const [selectedOption, setSelectedOption] = useState(null);

  const options = ['Option 1', 'Option 2', 'Option 3'];

  const handleOptionSelect = (option) => {
    setSelectedOption(option);
    setOpen(false); // Close the dropdown when an option is clicked
  };

  // Close the dropdown when clicking outside
  useEffect(() => {
    const handleOutsideClick = (event) => {
      if (!ref.current.contains(event.target)) {
        setOpen(false);
      }
    };

    document.addEventListener('mousedown', handleOutsideClick);

    return () => {
      document.removeEventListener('mousedown', handleOutsideClick);
    };
  }, [ref, setOpen]);

  return (
    <div className="custom-dropdown-container" ref={ref}>
      <Dropdown>
        <Dropdown.Toggle className="custom-dropdown-toggle" id="dropdown-basic">
          {selectedOption || 'Select an option'}
        </Dropdown.Toggle>

        <Dropdown.Menu className={`custom-dropdown-menu ${open ? 'open' : ''}`}>
          {options.map((option, index) => (
            <Dropdown.Item
              key={index}
              className="custom-dropdown-item"
              onClick={() => handleOptionSelect(option)}
            >
              {option}
            </Dropdown.Item>
          ))}
        </Dropdown.Menu>
      </Dropdown>
    </div>
  );
});

export default handleClickOutside(DropdownList); // Wrap with handleClickOutside HOC
