import React, { Component } from 'react';
import './SignIn.css'; // Your custom styles

class SignIn extends Component {
  constructor() {
    super();
    this.state = {
      email: '',
      password: '',
      emailError: '',
      passwordError: '',
    };
  }

  handleInputChange = (event) => {
    const { name, value } = event.target;
    this.setState({
      [name]: value,
    });
  };

  validate = () => {
    let emailError = '';
    let passwordError = '';

    if (!this.state.email) {
      emailError = 'Email field is required';
    }

    if (!this.state.password) {
      passwordError = 'Password field is required';
    }

    if (emailError || passwordError) {
      this.setState({ emailError, passwordError });
      return false;
    }

    return true;
  };

  handleSubmit = () => {
    if (this.validate()) {
      console.warn(this.state);
      // Reset state after submission
      this.setState({
        email: '',
        password: '',
        emailError: '',
        passwordError: '',
      });
    }
  };

  render() {
    return (
      <div className="sign-in-container">
        <div className="form-container">
          <h3 className="login-heading">Welcome back!</h3>
          <form>
            <div className="form-floating mb-3">
              <input
                type="email"
                className={`form-control ${this.state.emailError ? 'invalid' : ''}`}
                name="email"
                placeholder="name@example.com"
                value={this.state.email}
                onChange={this.handleInputChange}
              />
              <label>Email address</label>
              <span className="text-danger">{this.state.emailError}</span>
            </div>
            <div className="form-floating mb-3">
              <input
                type="password"
                className={`form-control ${this.state.passwordError ? 'invalid' : ''}`}
                name="password"
                placeholder="Password"
                value={this.state.password}
                onChange={this.handleInputChange}
              />
              <label>Password</label>
              <span className="text-danger">{this.state.passwordError}</span>
            </div>

            <div className="d-grid">
              <button
                className="btn btn-lg btn-login text-uppercase fw-bold mb-2"
                type="button"
                onClick={this.handleSubmit}
              >
                Sign in
              </button>
              <div className="text-center">
                <a className="small" href="#">
                  Forgot password?
                </a>
              </div>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

export default SignIn;
