import React, { useState, useEffect, useRef } from 'react';
import Dropdown from 'react-bootstrap/Dropdown';
import './DropdownList.css'; // Import your custom CSS file

function DropdownList() {
  const [selectedOption, setSelectedOption] = useState(null);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const options = ['Kiên Giang', 'An Giang', 'Cần Thơ', 'Long Xuyên', 'Đồng Th'];
  const dropdownRef = useRef(null);

  const handleOptionSelect = (option) => {
    setSelectedOption(option);
    setIsDropdownOpen(false);
  };

  const handleOutsideClick = (event) => {
    if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
      setIsDropdownOpen(false);
    }
  };

  useEffect(() => {
    window.addEventListener('mousedown', handleOutsideClick);
    return () => {
      window.removeEventListener('mousedown', handleOutsideClick);
    };
  }, []);

  return (
    <div ref={dropdownRef} className="custom-dropdown-container">
      <Dropdown show={isDropdownOpen} onToggle={(isOpen) => setIsDropdownOpen(isOpen)}>
        <Dropdown.Toggle className="custom-dropdown-toggle" id="dropdown-basic">
          {selectedOption || 'Nơi xuất phát'}
        </Dropdown.Toggle>

        <Dropdown.Menu className="custom-dropdown-menu">
          {options.map((option, index) => (
            <Dropdown.Item
              key={index}
              className="custom-dropdown-item"
              onClick={() => handleOptionSelect(option)}
            >
              {option}
            </Dropdown.Item>
          ))}
        </Dropdown.Menu>
      </Dropdown>
    </div>
  );
}

export default DropdownList;
