import React, {useState, useEffect, useRef} from 'react';
import Dropdown from 'react-bootstrap/Dropdown';
import './DropdownList.css';
import handleClickOutside from './HandleClickEvent'; // Import the handleClickOutside HOC

function DropdownList() {
  const [open, setOpen] = useState(false);

  let menuRef = useRef();

  useEffect(() => {
    let handler = (e)=>{
      if(!menuRef.current.contains(e.target)){
        setOpen(false);
        console.log(menuRef.current);
      }      
    };

    document.addEventListener("mousedown", handler);
    

    return() =>{
      document.removeEventListener("mousedown", handler);
    }

  });


  return (
    <div className="custom-dropdown-container" ref={menuRef}>
      <Dropdown>
        <Dropdown.Toggle className="custom-dropdown-toggle" id="dropdown-basic">
          {selectedOption || 'Select an option'}
        </Dropdown.Toggle>

        <Dropdown.Menu className="custom-dropdown-menu" onClick={()=>{setOpen(!open)}}>
          {options.map((option, index) => (
            <Dropdown.Item
              key={index}
              className="custom-dropdown-item"
              onClick={() => handleOptionSelect(option)}
            >
              {option}
            </Dropdown.Item>
          ))}
        </Dropdown.Menu>
      </Dropdown>
    </div>
  );
}

export default handleClickOutside(DropdownList); // Wrap with handleClickOutside HOC
