import React, { useState } from "react";
import './Main.css';

const Main = () => {
    const options = ["Option 1", "Option 2", "Option 3"];

    const [selectedOption, setSelectedOption] = useState("");
  
    const handleDropdownChange = (event) => {
        setSelectedOption(event.target.value);
    };

    return (
        <section className='Main'>
            <div className="overlay"></div>
            <div className="backgroundImage" style={{ backgroundImage: "url('/background.jpg')" }}></div>
            <div className="mainContent container">
                <div className="cardDiv grid">
                    <span className="textDiv">Tìm kiếm chuyến đi</span>
                </div>
                <div className="banner">
                    <form action="#">
                        <div className="form-row">
                            <div className="input-data">
                                <input type="text" required />
                                <label htmlFor="">Nơi xuất phát</label>
                            </div>
                            <img src="/flip.png" className="flip-icon" />
                            <div className="input-data">
                                <select id="dropdown" onChange={handleDropdownChange} required>
                                    {options.map((option, index) => (
                                        <option key={index} value={option}>
                                            {option}
                                        </option>
                                    ))}
                                </select>
                                <label htmlFor="">Nơi đến</label>
                            </div>
                            <img src="/calendar.png" className="calendar-icon" />
                            <div className="input-data">
                                <input
                                    type="text"
                                    id="inputText"
                                    placeholder="Selected text will appear here"
                                    value={selectedOption}
                                    readOnly
                                    required
                                />
                                <label htmlFor="">Ngày đi</label>
                            </div>
                        </div>
                    </form>
                    <button className="btn-search">Tìm chuyến</button>
                </div>
            </div>
        </section>
    );
}

export default Main;
